import React from 'react';
import { Card, Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './ProductList.css'; // Подключаем стили

const ProductList = ({ products = [], addToCart }) => {
    if (!Array.isArray(products)) {
        products = [];
    }

    return (
        <div className="container">
            <div className="row card-container">
                {products.map(product => (
                    <div className="col-md-3" key={product.id}>
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} className="product-image" />
                            <Card.Body className="card-body">
                                <Card.Title>{product.name}</Card.Title>
                                <Card.Text>${product.price.toFixed(2)}</Card.Text>
                                <Form.Control as="select" defaultValue={1} onChange={(e) => addToCart(product, parseInt(e.target.value))}>
                                    {[...Array(20).keys()].map((x) => (
                                        <option key={x + 1} value={x + 1}>
                                            {x + 1}
                                        </option>
                                    ))}
                                </Form.Control>
                            </Card.Body>
                            <Card.Footer className="card-footer">
                                <Button as={Link} to={`/product/${product.id}`} variant="primary">View Details</Button>
                                <Button variant="success" onClick={() => addToCart(product)}>Add to Cart</Button>
                            </Card.Footer>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductList;
