import React, { useState, useEffect } from 'react';
import { Container, Image, Button, ListGroup, Row, Col, Form } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = ({ addToCart }) => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);

    useEffect(() => {
        fetch('/products.json')
            .then(response => response.json())
            .then(data => {
                const foundProduct = data.find(p => p.id === parseInt(id));
                setProduct(foundProduct);
            })
            .catch(error => console.error('Error fetching product:', error));
    }, [id]);

    if (!product) {
        return <p>Product not found</p>;
    }

    const handleAddToCart = () => {
        addToCart(product, quantity);
    };

    return (
        <Container>
            <Row>
                <Col md={6}>
                    <Image src={product.image} fluid className="product-image" />
                </Col>
                <Col md={6}>
                    <h1>{product.name}</h1>
                    <ListGroup className="mt-3">
                        <ListGroup.Item><strong>Price:</strong> ${product.price}</ListGroup.Item>
                        <ListGroup.Item><strong>Description:</strong> {product.description}</ListGroup.Item>
                    </ListGroup>
                    <Form.Group className="mt-3">
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control
                            type="number"
                            min="1"
                            max="20"
                            value={quantity}
                            onChange={(e) => setQuantity(e.target.value)}
                        />
                    </Form.Group>
                    <Button variant="success" className="mt-3" onClick={handleAddToCart}>Add to Cart</Button>
                </Col>
            </Row>
        </Container>
    );
};

export default ProductDetail;
