import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './components/Header';
import ProductList from './components/ProductList';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';
import OrderHistory from './components/OrderHistory';
import AdminProductList from './components/AdminProductList';
import CreateCustomerForm from './components/CreateCustomerForm';
import CustomerList from './components/CustomerList';
import CreateProductForm from './components/CreateProductForm';
import UpdateCustomerForm from './components/UpdateCustomerForm';
import UpdateProductForm from './components/UpdateProductForm';

const App = () => {
    const [cartItems, setCartItems] = useState([]);
    const [customers, setCustomers] = useState([]);

    const addToCart = (product, quantity) => {
        setCartItems(prevItems => {
            const itemIndex = prevItems.findIndex(item => item.id === product.id);
            if (itemIndex >= 0) {
                const updatedItems = [...prevItems];
                updatedItems[itemIndex].quantity += quantity;
                return updatedItems;
            } else {
                return [...prevItems, { ...product, quantity }];
            }
        });
    };

    const updateCustomer = (updatedCustomer) => {
        setCustomers(prevCustomers => {
            return prevCustomers.map(customer =>
                customer.id === updatedCustomer.id ? updatedCustomer : customer
            );
        });
    };

    return (
        <Router>
            <Header />
            <Switch>
                <Route path="/products" component={ProductList} />
                <Route path="/product/:id" render={(props) => <ProductDetail {...props} addToCart={addToCart} />} />
                <Route path="/cart" render={(props) => <Cart {...props} cartItems={cartItems} />} />
                <Route path="/order-history" component={OrderHistory} />
                <Route path="/admin-products" component={AdminProductList} />
                <Route path="/create-customer" component={CreateCustomerForm} />
                <Route path="/customer-list" render={(props) => <CustomerList {...props} customers={customers} />} />
                <Route path="/create-product" component={CreateProductForm} />
                <Route path="/update-customer/:id" render={(props) => <UpdateCustomerForm {...props} customers={customers} updateCustomer={updateCustomer} />} />
                <Route path="/update-product/:id" render={(props) => <UpdateProductForm {...props} />} />
                <Route path="/" exact component={ProductList} />
            </Switch>
        </Router>
    );
};

export default App;
