import React, { useState, useEffect } from 'react';
import { Container, Image, Button, ListGroup } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import './ProductDetail.css'; // Убедитесь, что путь к файлу стилей правильный

const ProductDetail = ({ addToCart }) => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);

    useEffect(() => {
        // Запрос данных о продукте по ID из файла products.json
        fetch('/products.json')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                const foundProduct = data.find(p => p.id === parseInt(id));
                setProduct(foundProduct);
            })
            .catch(error => console.error('Error fetching product:', error));
    }, [id]);

    if (!product) {
        return <p>Product not found</p>;
    }

    const handleAddToCart = () => {
        addToCart(product, quantity);
    };

    return (
        <Container>
            <h1>{product.name}</h1>
            <Image src={product.image} fluid className="product-image" />
            <ListGroup className="mt-3">
                <ListGroup.Item><strong>Price:</strong> ${product.price}</ListGroup.Item>
                <ListGroup.Item><strong>Description:</strong> {product.description}</ListGroup.Item>
            </ListGroup>
            <input
                type="number"
                min="1"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                className="mb-2"
            />
            <Button variant="success" className="mt-3" onClick={handleAddToCart}>Add to Cart</Button>
        </Container>
    );
};

export default ProductDetail;
